import React, { useState } from "react";

function CreateArea() {
  const [note, setNote] = useState({
    title: "",
    content: "",
  });

  function handleClick(event){
      const name = event.target.name;

      setNote(prevNote => {
        return {
          ...prevNote ,
          [name] : "" 
        }
      });
  }

  function handleChange(event){
      const name = event.target.name;
      const value = event.target.value;

      setNote(prevNote => {
        return {
          ...prevNote ,
          [name] : value 
        }
      });
  }

  return (
    <div>
      <form>
        <input
          onClick={handleClick}
          onChange={handleChange}
          value={note.title}
          name="title"
          placeholder="Title"
        />
        <textarea
          onClick={handleClick}
          onChange={handleChange}
          value={note.content}
          name="content"
          placeholder="Take a note..."
          rows="3"
        />
        <button>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
